import base64

def img2string(img, para):
    with open(img, 'rb') as f:
        string = base64.b64encode(f.read()).decode()
    with open('Image.py', 'a') as f:
        f.write('%s = base64.b64decode("%s")\n'%(para, string))

if __name__ == '__main__':
    img2string('donate/37-微信.png', 'img1')
    img2string('donate/赞赏码3副本2.png', 'img2')

img1 = base64.b64decode("")
img2 = base64.b64decode("")
